  pixmap_cni = create_pixmap (main_window, "test/AEL-GLOSSY-small.png");
  gtk_widget_set_name (pixmap_cni, "pixmap_cni");
  gtk_widget_show (pixmap_cni);
  gtk_container_add (GTK_CONTAINER (frame148), pixmap_cni);
  gtk_misc_set_padding (GTK_MISC (pixmap_cni), 2, 0);

  toolbar1 = gtk_toolbar_new ();
  gtk_widget_set_name (toolbar1, "toolbar1");
  gtk_widget_show (toolbar1);
  gtk_box_pack_start (GTK_BOX (hbox19), toolbar1, TRUE, TRUE, 3);
  gtk_toolbar_set_style (GTK_TOOLBAR (toolbar1), GTK_TOOLBAR_BOTH);
  gtk_toolbar_set_show_arrow (GTK_TOOLBAR (toolbar1), FALSE);
