  tmp_image = gtk_image_new_from_stock ("gtk-execute", tmp_toolbar_icon_size);
  gtk_widget_show (tmp_image);
  pb_test = (GtkWidget*) gtk_tool_button_new (tmp_image, _("Test"));
  gtk_widget_set_name (pb_test, "pb_test");
  gtk_widget_show (pb_test);
  gtk_container_add (GTK_CONTAINER (toolbar1), pb_test);
  gtk_tool_item_set_tooltip (GTK_TOOL_ITEM (pb_test), tooltips, _("Test"), NULL);

  tmp_image = gtk_image_new_from_stock ("gtk-print", tmp_toolbar_icon_size);
