  /* Store pointers to all widgets, for use by lookup_widget(). */
  GLADE_HOOKUP_OBJECT_NO_REF (main_window, main_window, "main_window");
  GLADE_HOOKUP_OBJECT (main_window, bonobodock1, "bonobodock1");
  GLADE_HOOKUP_OBJECT (main_window, mb_main_uiinfo[0].widget, "archivio_menu");
  GLADE_HOOKUP_OBJECT (main_window, archivio_menu_menu_uiinfo[0].widget, "pbm_print");
  GLADE_HOOKUP_OBJECT (main_window, archivio_menu_menu_uiinfo[1].widget, "separator1");
