  GLADE_HOOKUP_OBJECT (main_window, pixmap_cni, "pixmap_cni");
  GLADE_HOOKUP_OBJECT (main_window, toolbar1, "toolbar1");
  GLADE_HOOKUP_OBJECT (main_window, pb_test, "pb_test");
