  GLADE_HOOKUP_OBJECT (main_window, separatortoolitem2, "separatortoolitem2");
  GLADE_HOOKUP_OBJECT (main_window, pb_exit, "pb_exit");
  GLADE_HOOKUP_OBJECT (main_window, frame149, "frame149");
  GLADE_HOOKUP_OBJECT (main_window, pixmap_customer, "pixmap_customer");
  GLADE_HOOKUP_OBJECT (main_window, vbox183, "vbox183");
  GLADE_HOOKUP_OBJECT (main_window, vpaned_main, "vpaned_main");
  GLADE_HOOKUP_OBJECT (main_window, sw_main, "sw_main");
  GLADE_HOOKUP_OBJECT (main_window, hbox21, "hbox21");
